import { Button, Flex, Text } from '@chakra-ui/react';
import MotionBox from '../../../../motion/motionBox';
import { useEffect, useRef } from 'react';
import { useInView, useAnimation } from 'framer-motion';
import MotionHeading from '../../../../motion/motionHeading/motionHeading';
import { fadeInTop, mainBox } from '../../../../../utils/animation';
import { FaRegEye, FaRegEyeSlash } from 'react-icons/fa6';
import { useCovidStore } from '../../../../../stores/editableView';


export default function Covid() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  const mainControls = useAnimation();

  useEffect(() => {
    if (isInView) {
      mainControls.start('animate');
    }
  });
  const [showCovid, setShowCovid] = useCovidStore((state) => [
    state.showCovid,
    state.setShowCovid,
  ]);
  return (
    <MotionBox
      variants={mainBox}
      initial="initial"
      animate={mainControls}
      p="2rem 1rem"
      borderRadius="1.5rem"
      bg="bgWhiteOp80.50"
      my="1rem"
      sx={showCovid ? { opacity: 1 } : { opacity: '0.5 !important' }}
    >
      <Button
        leftIcon={showCovid ? <FaRegEye /> : <FaRegEyeSlash />}
        position="absolute"
        colorScheme="passPort"
        fontSize="0.7rem"
        borderRadius="2xl"
        top="-1"
        right="3"
        variant="rusticWeddingBloom"
        onClick={() => {
          setShowCovid(!showCovid);
        }}
        size="xs"
      >
        <Text mt="1">{showCovid ? 'Sembunyikan' : 'Tampilkan'}</Text>
      </Button>
      <MotionHeading
        variants={fadeInTop}
        textAlign="center"
        variant="rusticWeddingBloomMolgakClassy"
color="passPort.500"
        my="1rem"
      >
        Protokol Covid-19
      </MotionHeading>
      <Text
        textAlign="center"
        fontFamily="PoppinsRegular"
        mb="1.5rem"
        fontSize="0.8rem"
        ref={ref}
      >
        Kami menghimbau para tamu undangan agar tetap memperhatikan protokol
        kesehatan.
      </Text>
      <Flex justifyContent="space-between" alignItems="flex-end">
        <MotionBox
          variants={fadeInTop}
          display="flex"
          alignItems="center"
          flexDirection="column"
        >
          <IconHandSanitizer />
          <Text
            textAlign="center"
            fontFamily="PoppinsRegular"
            fontSize="0.8rem"
          >
            Memakai Hand Sanitizer
          </Text>
        </MotionBox>
        <MotionBox
          variants={fadeInTop}
          display="flex"
          alignItems="center"
          flexDirection="column"
        >
          <IconHandWash />
          <Text
            textAlign="center"
            fontFamily="PoppinsRegular"
            fontSize="0.8rem"
          >
            Rajin Mencuci Tangan
          </Text>
        </MotionBox>
        <MotionBox
          variants={fadeInTop}
          display="flex"
          alignItems="center"
          flexDirection="column"
        >
          <IconPhysicalDistancing />
          <Text
            textAlign="center"
            fontFamily="PoppinsRegular"
            fontSize="0.8rem"
          >
            Jaga Jarak Aman
          </Text>
        </MotionBox>
      </Flex>
    </MotionBox>
  );
}

